import { FormControl } from '@angular/forms';
import { Component, Input } from '@angular/core';
import { RatingService } from 'src/app/services/rating.service';
import { movie } from 'src/app/models/movie';


@Component({
  selector: 'app-star-rating',
  templateUrl: './star-rating.component.html',
  styleUrls: ['./star-rating.component.css']
})
export class StarRatingComponent {

constructor(private ratingService:RatingService){

}
votingOpen=false;

ratingFormControl=new FormControl(0);
onRating(){

return this.ratingService.rateTheMovie(this.ratingFormControl);

}

onSaveRating(movie) {
  let moviesDb = localStorage.getItem("movies");
  let movies:movie[];
    if (moviesDb != null) {
      movies = JSON.parse(moviesDb);
    } else {
      moviesDb = '';
      movies = JSON.parse(moviesDb);
    }
    let movieFoundIndex:movie = movies.findIndex(movie.Title);
    console.log(movieFoundIndex)
    localStorage.setItem("movies", JSON.stringify(this.movies));
    return this.movies;
  return this.ratingFormControl.value;
  }
}
