import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private authService:AuthService,
    private route:ActivatedRoute,
    private router:Router){}
  signInForm=new FormGroup({
    username: new FormControl('',Validators.required),
    password: new FormControl('', Validators.required)
  })

  isValid=true;
  isAdmin=false;
  currentUsername:string='';
  onSignIn(){
    this.authService.login(this.signInForm.value);
    this.isValid=this.authService.getIsLoggedIn();
    if(this.isValid){
      this.router.navigate(["/movies"]);
      this.currentUsername=this.authService.currentUser;
    }
    else{
      this.signInForm.reset();
    }
    if(this.authService.getIsAdmin()){
      this.isAdmin=this.authService.getIsAdmin();
    }
  }



}

<div class="container" >
  <div class="box btn btn-primary mb-3" >
  <h1 class="text-center">Please login to get access.</h1>
  <form [formGroup]="signInForm" (ngSubmit)="onSignIn()">
    <div *ngIf="!isValid" class="alert alert-danger">Invalid username and/or password!</div>
    <div class="form-group">
      <label for="username" class="float-start"><b>Username</b></label>
      <input type="text" class="form-control" id="username" formControlName="username">

    </div>
    <div class="form-group">
      <label for="password" class="float-start"><b>Password</b></label>
      <input type="password" class="form-control" id="password" formControlName="password">
    </div>
    <button type="submit" class="btn btn-info float-end"><b>Sign In</b></button>
  </form>
</div>


</div>
