import { Component } from '@angular/core';
import { MovieService } from 'src/app/services/movie.service';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';
import { movie } from 'src/app/models/movie';
import { FormControl } from '@angular/forms';
import { RatingService } from 'src/app/services/rating.service';
import { StarRatingComponent } from '../star-rating/star-rating.component';

@Component({
  selector: 'app-movie',
  templateUrl: './movie.component.html',
  styleUrls: ['./movie.component.css']
})
export class MovieComponent {

ratingControl= new FormControl();
getRating() {
console.log(this.ratingControl.value);
}


  movies: any[];
  isAdmin:boolean;
  isValid:boolean;
  username:string;
  votingOpen:boolean=false;


  constructor(private movieService:  MovieService, private user:AuthService, private router:Router, private rateService:RatingService) {
    this.movies = this.movieService.getMovies();
    this.isAdmin=user.getIsAdmin();
    this.isValid=user.getIsLoggedIn();
    this.username=user.currentUser;
}
onAddMovie(){
  this.router.navigate(['/add-movie']);
}
onRemoveMovie(movie:movie) {

  this.movies=this.movieService.deleteMovie(movie);
  }

onLogOut() {
  this.user.currentUser="";
  this.isValid=false;
  this.router.navigate(['']);
  return this.isValid;
  }

  onVote(movie:movie) {
    this.votingOpen=true;


    }

    onSavingVote(movie:movie){
      this.movies=this.movieService.onSavingVote(movie);
    }

    }


<div class="container">
  <nav class="navbar fixed-top navbar-dark bg-primary justify-content-end">
    <span class="navbar-text ml-0">Hello {{username}},</span>
    <button *ngIf="this.isAdmin" class="btn btn-info" (click)="onAddMovie()">Add Movie</button>


    <button class="btn btn-info" (click)="onLogOut()">Logout</button>

  </nav>

  <div class="row">
    <div *ngFor="let movie of movies, index as i" class="col-md-4 mb-4">
      <div class="flip-card">
        <div class="flip-card-inner">
          <div class="flip-card-front">
            <img [src]="movie.Poster" alt="{{ movie.Title }} Poster">
            <div class="card-body">
              <p class="card-text"><i class="fa fa-star checked"></i> {{ movie.AverageRating }}</p>
              <h5 class="card-title">{{ movie.Title }}</h5>
              <p class="card-text"><strong>Year:</strong> {{ movie.Year }}</p>
              <p class="card-text"><strong>Runtime:</strong> {{ movie.Runtime }}</p>

            </div>
          </div>

          <div class="flip-card-back">
            <p class="card-text"><strong>Genre:</strong> {{ movie.Genre }}</p>
            <p class="card-text"><strong>Actors:</strong> {{ movie.Actors }}</p>
            <p class="card-text"><strong>Plot:</strong> {{ movie.Plot }}</p>
          </div>
        </div>

      </div>
      <button *ngIf="votingOpen==false" >Vote</button>
      <div *ngIf="votingOpen==true">
      <app-star-rating ></app-star-rating>
      <button class="btn btn-info" (click)="onVote(movie)">Save</button>
    </div>
      <button *ngIf="this.isAdmin" class="btn btn-info" (click)="onRemoveMovie(movie)">Remove</button>
    </div>
  </div>

</div>
