import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { MovieService } from 'src/app/services/movie.service';

@Component({
  selector: 'app-ad-movie',
  templateUrl: './ad-movie.component.html',
  styleUrls: ['./ad-movie.component.css']
})
export class AdMovieComponent {
  addMovieForm: FormGroup;
  isAdmin:boolean;

  constructor(private movieService: MovieService, private router:Router, private authService:AuthService) {
    this.addMovieForm = new FormGroup({
      title: new FormControl('', Validators.required),
      year: new FormControl('', Validators.required),
      runtime: new FormControl('', Validators.required),
      genre: new FormControl('', Validators.required),
      actors: new FormControl('', Validators.required),
      plot: new FormControl('', Validators.required),
      poster: new FormControl('', Validators.required),

    });

    this.isAdmin=authService.getIsLoggedIn();
  }

  addMovie(): void {
    const { title, year, runtime, genre, actors, plot, poster } = this.addMovieForm.value;
    this.movieService.addMovie({ Title: title, Year: year, Runtime: runtime, Genre: genre, Actors: actors, Plot: plot, Poster: poster });
    this.addMovieForm.reset();
    this.router.navigate(["/movies"]);
  }
}
<div class="container">
  <div class="bg-primary p-5">
  <form [formGroup]="addMovieForm" (ngSubmit)="addMovie()">
    <div class="form-group">
      <label for="title" class="float-start text-light-900">Title</label>
      <input type="text" class="form-control" id="title" formControlName="title">

    </div>
    <div class="form-group">
      <label for="year" class="float-start">Year</label>
      <input type="text" class="form-control" id="year" formControlName="year">

    </div>
    <div class="form-group">
      <label for="runtime" class="float-start">Runtime</label>
      <input type="text" class="form-control" id="runtime" formControlName="runtime">

    </div>
    <div class="form-group">
      <label for="genre" class="float-start">Genre</label>
      <input type="text" class="form-control" id="genre" formControlName="genre">

    </div>
    <div class="form-group">
      <label for="actors" class="float-start">Actors</label>
      <input type="text" class="form-control" id="actors" formControlName="actors">

    </div>
    <div class="form-group">
      <label for="plot" class="float-start">Plot</label>
      <input type="text" class="form-control" id="plot" formControlName="plot">

    </div>
    <div class="form-group">
      <label for="poster" class="float-start">Poster</label>
      <input type="text" class="form-control" id="poster" formControlName="poster">

    </div>


    <button type="submit" class="btn btn-info float-end" >Add Movie</button>
  </form>
</div>
</div>
