import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private authService:AuthService,
    private route:ActivatedRoute,
    private router:Router){}
  signInForm=new FormGroup({
    username: new FormControl('',Validators.required),
    password: new FormControl('', Validators.required)
  })

  isValid=true;
  onSignIn(){
    this.authService.login(this.signInForm.value);
    this.isValid=this.authService.getIsLoggedIn();
    if(this.isValid){
      this.router.navigate(["/movies"])
    }
    console.log(this.authService.login(this.signInForm.value));
    console.log(this.signInForm.value);
    console.log(this.authService.getIsLoggedIn());
    console.log(this.authService.getIsAdmin());
  }
  get username(){
    return this.signInForm.get('username');
  }
}

<div class="container ">
  <h1 class="text-center">Please login to get acces.</h1>
  <form [formGroup]="signInForm" (ngSubmit)="onSignIn()">
    <div *ngIf="!isValid" class="alert alert-danger">Invalid username and/or password!</div>
    <div class="form-group">
      <label for="username">Username</label>
      <input type="text" class="form-control" id="username" formControlName="username">
      <div *ngIf="username?.touched&&username?.invalid" class="alert alert-danger">Username is required</div>
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" id="password" formControlName="password">
    </div>
    <button type="submit" class="btn btn-primary float-end">Sign In</button>
  </form>


</div>
<div class="container messagebottom">You don't have an account? Please <a href="/sign-up-form">Sign Up</a> for free.</div>
